                IsVpdSect = False
                IsUpdSect = False
                IsTmpSect = False
                SectionName = Match.group(1).lower()
                if  SectionName == "Defines".lower():
                    IsDefSect = True
                if  (SectionName == "PcdsFeatureFlag".lower() or SectionName == "PcdsFixedAtBuild".lower()):
                    IsPcdSect = True
                elif SectionName == "PcdsDynamicVpd.Tmp".lower():
                    IsTmpSect = True
